function sortTable(a){var j,k,b,e,h,g,f,c,d=0;j=document.getElementById("notelistTable");b=true;c="asc";while(b){b=false;k=j.getElementsByTagName("TR");for(e=1;e<(k.length-1);e++){f=false;h=k[e].getElementsByTagName("TD")[a];g=k[e+1].getElementsByTagName("TD")[a];if(c=="asc"){if(h.innerHTML.toLowerCase()>g.innerHTML.toLowerCase()){f=true;break}}else{if(c=="desc"){if(h.innerHTML.toLowerCase()<g.innerHTML.toLowerCase()){f=true;break}}}}if(f){k[e].parentNode.insertBefore(k[e+1],k[e]);b=true;d++}else{if(d==0&&c=="asc"){c="desc";b=true}}}}function filterTable(){var a,c,d,e,f,b;a=document.getElementById("filterNotes");c=a.value.toUpperCase();d=document.getElementById("notelistTable");e=d.getElementsByTagName("tr");for(b=0;b<e.length;b++){f=e[b].getElementsByTagName("td")[0];if(f){if(f.innerHTML.toUpperCase().indexOf(c)>-1){e[b].style.display=""}else{e[b].style.display="none"}}}};