var textareaAdd=document.getElementById("contentAdd");textareaAdd.onkeydown=function(b){if(b.keyCode===9||b.which===9){b.preventDefault();var a=this.selectionStart;this.value=this.value.substring(0,this.selectionStart)+"\t"+this.value.substring(this.selectionEnd);this.selectionEnd=a+1}if(b.keyCode===13||b.which===13){b.preventDefault();document.getElementById("content").value+="\n"+textareaAdd.value;this.value=""}};textareaAdd.focus();