function copyToClipboard(b){var a=document.createElement("textarea");if(b=="copyURL"){a.value=window.location.href}else{if(b=="copyURLViewOnly"){a.value=window.location.href+"?view"}else{a.value=document.getElementById(b).innerHTML}}document.body.appendChild(a);a.select();document.execCommand("copy");document.body.removeChild(a);document.getElementById("copyMessage").innerHTML="<br>Copied"};