function navbarResponsive(){var a=document.getElementById("navbar");if(a.className==="navbar"){a.className+=" responsive"}else{a.className="navbar"}}var menuButton=document.getElementById("menuButton");function windowOnClick_navbar(b){if(b.target.id!=="menuButton"){var a=document.getElementById("navbar");if(a.className.indexOf("responsive")>-1){a.className="navbar"}}}window.addEventListener("click",windowOnClick_navbar);function downloadFile(){var b=document.createElement("a");var a=document.getElementById("content").value;b.href="data:application/txt,"+encodeURIComponent(a);b.download="note_"+document.title+".txt";b.dispatchEvent(new MouseEvent("click",{bubbles:true,cancelable:true,view:window}))}function toggleMonospace(b){var a=document.getElementById("content");b.innerHTML=(b.innerHTML=="mono")?"sans":"mono";if(a.style.fontFamily=="monospace"){a.style.fontFamily="sans-serif"}else{a.style.fontFamily="monospace"}}function deleteFile(){var b=confirm("Are you sure you want to delete this note?");if(b==true){document.getElementById("content").value="";uploadContent();alert("Note deleted");var a=window.location.href;window.location.href=a.substring(0,a.lastIndexOf("/"))}};